firstfeature = Vector(1:10)
secondfeature = [8, 3, 5, 2, 1, 11, 2, 3, 7, 4]
thirdfeature = [8, 2, 3, 8, 4, 2, 1, 22, 3, 4]
fourth_feature = [95, 23, 22, 35, 20, 20, 29, 16, 65, 52]

#==output[code]
10-element Vector{Int64}:
 95
 23
 22
 35
 20
 20
 29
